() => {
		const count = {}; 
		const uuidBooks = {};
		let dataFromMap = main.store.dataFromMap;
		if(!dataFromMap) {
			dataFromMap = main.store.InitMapData;
		}

		for (let data of dataFromMap) {			
				let dataTime = data.year_of_publication;
			  const item = {
            title: data.title,
            keywords: data.keywords,
            place_of_publication: data.place_of_publication,
            year_of_publication: data.year_of_publication,
            author: data.author,
            status: data.status,
            city: data.city
        };
				count[dataTime] = count[dataTime] ? count[dataTime] + 1 : 1;
				uuidBooks[dataTime] = uuidBooks[dataTime] ? [...uuidBooks[dataTime], item] : [item];

		}
		 const res = Object.entries(count).map(i => ({time: i[0], count: i[1], books: uuidBooks[i[0]]}));
		 //return res;
		const result = [];
		let start=parseInt(res[0].time);
		const end= parseInt(res[res.length-1].time)
		for( let i=start; i<end;i++){
			let check=res.filter(el=> el.time==i);
			if(check && check.length > 0){
				result.push(check[0]);				
			}
			else{
				result.push({"time":String(i),"count":0})
			}
		}



		return result;

	}

