actionConfiguration.body:
async () => {
		if(!Object.entries(main.store).length) {
			// Will call only old data saved in Local Storage
			return;
		}
		let arr = main.store.InitMapData;


		/*  Categories:   */
		if (List1Copy.selectedItem.name && List1Copy.selectedItem.name !== "<b>ALL</b>") {
			if (List1Copy.selectedItem.name === 'No category') {
				arr = arr.filter(el=>!el.keywords)
			} else {
		   arr = arr.filter(el => el.keywords.toLocaleLowerCase() == List1Copy.selectedItem.name.toLocaleLowerCase())
			}
		}


		/*  Map:   */ 
		if (Object.entries(MapOfBooksPublication.getBounds).length) {
			arr = arr.filter(el => (
				el.lon >
				MapOfBooksPublication.getBounds.west && el.lon <
				MapOfBooksPublication.getBounds.east &&  el.lat > 
				MapOfBooksPublication.getBounds.south && el.lat < 
				MapOfBooksPublication.getBounds.north
			))
		}


		/*  Timeline:   */
		if (LargeAreaChart.filteredData && LargeAreaChart.filteredData.length > 0) { 
				arr = arr.filter(el => el.year_of_publication >= LargeAreaChart.filteredData[0].time && el.year_of_publication <= LargeAreaChart.filteredData[LargeAreaChart.filteredData.length - 1].time);
		}

		const count = {}; 
		for (let data of arr) {			
				let dataTime = data.year_of_publication;
				count[dataTime] = count[dataTime] ? count[dataTime] + 1 : 1;
		}
		const res = Object.entries(count).map(i => ({time: i[0], count: i[1]}));
		let result = [];

		if (res && res.length > 0) {
			let start = parseInt(year_from_field.selectedDate.slice(0, 10));
			const end = parseInt(year_to_field.selectedDate.slice(0, 10));

			for(let i = start; i <= end; i++){
				result.push({ "time": String(i), "count": count[i] ? count[i] : 0 })
			}
		}


		storeValue("timlineDataGlobal", result)
		storeValue("dataFromMap", arr)
		//arr = arr.length ? arr : main.store.InitMapData;
		return arr;
	}

